const StateBar = styled.div`
    display: flex;
    justify-content: space-between;
    width: 100%;
    height: 80px;
    padding: 20px;
    /* background-color: red; for test*/
    position: static;
`;

const StateBarColumn = styled.div`
`;


export default Menubar;
