import React, {useState, useEffect}from 'react';
import styled from 'styled-components';
import picturesUrl from '../../dummyData/dummyPictures';
import Menubar from './Menubar';
import ViewDetail from './ViewDetail';

function Gallery() {
    let forCleanUp = true;
    const [pictures, setPictures] = useState([])
    const [viewDetail, setViewDetail] = useState(false);
    const [seleted, setSeleted] = useState(undefined);

    useEffect(()=>{
        if(forCleanUp){
            setPictures(picturesUrl);
            // setSeleted(pictures[0].id);
            // 웹 서버 구현하면 여기서 Ajax요청할 것
        }
        return ()=>{
            forCleanUp = false;
        }
    },[]);

    console.log('pictures : ', pictures);

    return (
        <>
        <Menubar />
        <Div>
            {!viewDetail ? pictures.map((el, idx)=><div key={idx}>
                <Img src={el} onClick={()=>{
                    setViewDetail(!viewDetail);
                    setSeleted(idx);
                }}></Img></div>) : <ViewDetail/>}
        </Div>
        </>
    )
}

const Div = styled.div`
    text-align: center;
`;
const Img = styled.img`
    /* padding: 25px; */
    width: 70%;
    height: 30%;
`;

export default Gallery
