import initalState from './initialState';

export const reducerMenu = (state = initalState, action) => {
    console.log('action : ', action.category);
    switch(action.type){
        case 'OPEN_MENU': 
            return Object.assign({}, state, {menu: true, category: action.category});
        case 'CLOSE_MENU':
            return Object.assign({}, state, {menu: false});
        default:
            return state;
    }
}
