import React from 'react'
import { useSelector, useDispatch } from 'react-redux';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { Motion, spring } from 'react-motion';

function MenuView() {
    const dispatch = useDispatch();
    const Menus = useSelector(state => {
        return state.reducerMenu.menus;
    });
    const isOpenMenu = useSelector(state => {
        return state.reducerMenu.menu;
    })
    const galleryMenu = useSelector(state => {
        return state.reducerMenu.galleryMenu;
    })

    const galleryMenus = useSelector(state => {
        return state.reducerMenu.galleryMenus
    })

    let temp = isOpenMenu ? '0px' : '-200px';


    const SideMenuBar = styled.div`
        width: 200px;
        height: 100%;
        background-color: white;
        position: fixed;
        top: 0;
        z-index: 1;
        left: ${temp};
        /* transition: all 1s; */
        margin-top: 80px;
    `;

    const Menu = styled.div`
    margin: 20px;
    font-size: 20px;
    text-align: center;
    text-decoration-line: none;
    `;

    return (
        <>
            <Motion
                defaultStyle={{ x: -200, opacity: 0 }}
                style={{ x: 0, opacity: spring(isOpenMenu ? 1 : 0) }}
            >
                {(style) => (
                    <>
                        {galleryMenu ? <SideMenuBar style={{ opacity: style.opacity }}>
                            {galleryMenus.map((el) => {
                                console.log('galleryMenus.map 실행됌');
                                <>
                                <h1 className="test" sytle={{ color: 'red'}}>galleryMenus.map 실행됌</h1>
                                <Link to={`/${el.title}`} style={{ textDecoration: 'none', color: 'black' }}
                                    onClick={() => {
                                        dispatch({
                                            type: 'CLOSE_AND_CHEANGE_CATEGORY',
                                            payload: {
                                                category: el.title
                                            }
                                        });

                                    }}><Menu key={el.id}>{el.title}</Menu></Link></>
                            })}
                        </SideMenuBar>
                            : <SideMenuBar style={{ opacity: style.opacity }}>
                                {Menus.map((el) => <Link to={`/${el.title === 'Gallery' ? '' : el.title}`} style={{ textDecoration: 'none', color: 'black' }}
                                    onClick={() => {
                                        if (el.title === 'Gallery') {
                                            dispatch({
                                                type: 'OPEN_GALLERY_MEUNS'
                                            })
                                        } else {
                                            dispatch({
                                                type: 'CLOSE_AND_CHEANGE_CATEGORY',
                                                payload: {
                                                    category: el.title
                                                }
                                            });
                                        }
                                    }}><Menu key={el.id}>{el.title}</Menu></Link>)}
                            </SideMenuBar>}
                    </>
                )}
            </Motion>
        </>
    )
}

export default MenuView

/*
    트랜지션 효과: 모든 프로퍼티의 변화를 2초에 걸쳐 전환한다.
